import UserButton from "./UserButton";
import RadioButton from "./RadioButton";
import React, { useState, useEffect, useReducer} from 'react';
import  {saveTransaction, loadData}  from '../actions'
import { connect } from 'react-redux';
import reducer, {exampleInitialState} from '../reducer';



const btnName = [{
    id: 1,
    name: 'USER-A'
    },
    {
        id: 2,
        name: 'USER-B'
        },
        {
            id: 3,    
            name: 'USER-C'
            },
];
const radioBtn = [{
    id: 1,
    name: 'American Express'
    },
    {
        id: 2,
        name: 'VISA'
        },
        {
            id: 3,
            name: 'DBS PayLa'
            },
];

function Example(props) {
    // Declare a new state variable, which we'll call "count"
    const [transactionId, setTransactionId] = useState(0);
    const [userName, setUserName] = useState(0);
    const [paymentMode, setPaymentMode] = useState(0);
    const [amount, setAmount] = useState(0);
    const [state, dispatch] = useReducer(reducer, exampleInitialState);


    // useEffect(
    //         props.dispatch(saveTransaction({
    //         transactionId,
    //         userName,
    //         paymentMode,
    //         amount
    //       })
    //     ));
    const handleSubmit = (evt) => {
      props.dispatch(loadData());
      // const abc = fetch('http://localhost:3000/rest/test')
      console.log('I am in Handler');
  }

    return (
      <div>
            <form onSubmit={handleSubmit} >        
            {/* <form onSubmit={() => dispatch({type: 'saveTransaction'})}>         */}
            <div className="formButton">
            {
                  btnName.map((item) => {
                   return  <UserButton name={item.name} onClickHandler={(name) => { setUserName(name)}}/>
                  })
              }
            </div>
            <div className="formButton radioPosition">
            {
                  radioBtn.map(item => {
                   return  <RadioButton name={item.name} onClickHandler={(payment) => { setPaymentMode(payment)}}/>
                  })
              }
            </div>
              <input type="text" onChange={(e) => { setAmount(e.target.value)}}/>
              <input type="submit" value="Transfer" />
              {/* <p>{userName}</p>
              <p>{paymentMode}</p>
              <p>{amount}</p> */}

            </form>
            <style jsx>{`
        form {
          background: #d9d8d9;
  margin: auto;
  width: 75%;
  padding: 21px;
        }
        .formButton {
          width: 33%;
          display: inline-block;
        }
        button {
          display: block;
        }
        .
      `}</style>
         </div>
    );
  }

  function mapStateToProps(state) {
    return {
        transactionId: state.transactionId,
        userName: state.userName,
        paymentMode: state.paymentMode,
        amount: state.amount
    }
  }

  function mapDispatchToProps(dispatch) {
    return {};
  }
  // export default connect(mapStateToProps, mapDispatchToProps)(Example);
  export default connect(mapStateToProps)(Example);
