import React from 'react';
import Loader from './Loader';

function Table(props) {
  console.log('props in Table', props);
  const tableData = props.data;
  console.log('tableDta', tableData);
  // console.log('tableDta', tableData.length);


  const tData = (tableData && tableData.length) ?
    tableData.map(i => {
      return(
        <>
       <tr className="rowClass">
        <td>{i.transactionId}</td>
        <td>{i.userName}</td>
        <td>{i.paymentMode}</td>
        <td>{i.amount}</td>
      </tr>
      <style jsx>{`
             .rowClass , td{
            border: 1px solid #000 !important;
            padding: 5px;
            text-align: center;
        }
        `}</style>
      </>
         )
       }) : <p>Loading ...</p>

 return (
    <div className="tableDiv">
       <table>
         <tr>
           <th>Transaction ID</th>
           <th>User Name</th>
           <th>Payment Mode</th>
           <th>Amount</th>
         </tr>
         <tbody>
           {tData}
         </tbody>
       </table>
       <style jsx>{`
      .tableDiv{
          padding: 1rem;
          margin: 1rem;
        }
        table{
          /*width:100%; */
          display: block;
          empty-cells: show;

          /* Decoration */
          border-spacing: 0;
          border: 1px solid;
        }

        table thead{
          background-color: #f1f1f1;  
          position:relative;
          display: block;
          width:100%;
          overflow-y: scroll;
        }

        table tbody{
          /* Position */
          display: block; position:relative;
          width:100%; overflow-y:scroll;
          /* Decoration */
          border-top: 1px solid rgba(0,0,0,0.2);
        }

        table tr{
          width: 100%;
          display:flex;
        }

        table td,.table th{
          flex-basis:100%;
          flex-grow:2;
          display: block;
          padding: 1rem;
          text-align:left;
        }

        /* Other options */

        table td:first-child,
        table th:first-child{
          flex-basis:20%;
          flex-grow:1;
        }

        tbody{
          max-height: 50vh;
        }


             `}</style>
           </div>

      )
}

export default Table;
