import React from 'react';
import Loader from './Loader';

function Table(props) {
  console.log('props in Table', props);
  const tableData = props.data;
  console.log('tableDta', tableData);
  // console.log('tableDta', tableData.length);


  const tData = (tableData && tableData.length) ?
    tableData.map(i => {
      return(
        <>
       <tr className="rowClass">
        <td>{i.transactionId}</td>
        <td>{i.userName}</td>
        <td>{i.paymentMode}</td>
        <td>{i.amount}</td>
      </tr>
      <style jsx>{`
             .rowClass , td{
            border: 1px solid #000 !important;
            padding: 5px;
            text-align: center;
        }
        `}</style>
      </>
         )
       }) : <p>Loading ...</p>

 return (
    <div className="tableDiv">
       <table>
         <tr>
           <th>Transaction ID</th>
           <th>User Name</th>
           <th>Payment Mode</th>
           <th>Amount</th>
         </tr>
         <tbody>
           {tData}
         </tbody>
       </table>
       <style jsx>{`
       table, th, td {
           border: 1px solid #7a7a7a;
           border-collapse: collapse;
           background: #ccc;
           font-weight: normal;
           font-family: sans-serif;
           font-size: 12px;
           width: 100%;
         }
          th {
           border-bottom: 2px solid #010101;
          }
          tbody {
           overflow-x: auto;
          }
         th, td {
           padding: 5px;
           text-align: center;
           width: 25%;
         }
           .rowClass{
          border: 1px solid #000 !important;
          padding: 5px;
          text-align: center;
      }

         .tableDiv {
           background: #d9d8d9;
           margin: auto;
           width: 75%;
           padding: 21px;
           margin-top: 15px;
         }
         .table-container {
          height: 10em;
      }
      table {
          display: flex;
          flex-flow: column;
          height: 100%;
          width: 100%;
      }
      table thead {
          /* head takes the height it requires, 
          and it's not scaled when table is resized */
          flex: 0 0 auto;
          width: calc(100% - 0.9em);
      }
      table tbody {
          /* body takes all the remaining available space */
          flex: 1 1 auto;
          display: block;
          overflow-y: scroll;
      }
      table tbody tr {
          width: 100%;
      }
      table thead,
      // table tbody tr {
      //     display: table;
      //     table-layout: fixed;
      // }
             `}</style>
           </div>

      )
}

export default Table;
